import React, { Fragment } from "react";
import ListItem from "../common/ListItem";
import { Button } from "@chakra-ui/core";
import useRequest from "../../hooks/useRequest";
import { useSWRPages } from "swr";

const List = () => {
  const { pages, isLoadingMore, loadMore } = useSWRPages(
    "list",
    ({ offset, withSWR }) => {
      const url = `https://gateway.marvel.com:443/v1/public/characters?apikey=2f1fed568f42c3ac72c3e50846da7949?offset=${offset ||
        0}`;
      // eslint-disable-next-line
      const { data } = withSWR(useRequest({ url }));
      if (!data) return null;

      const { results } = data;
      return results.map(result =>
        result.data.results.map(item => <ListItem key={item.id} {...item} />)
      );
    },
    []
  );

  return (
    <Fragment>
      {}
      <Button mt={3} w="100%">
        Load more
      </Button>
    </Fragment>
  );
};

export default List;
