import React, { Suspense } from "react";
import MainLayout from "./modules/main";
import { CSSReset, ThemeProvider } from "@chakra-ui/core";
import CustomTheme from "./theme";
import { SWRConfig } from "swr";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import ListHeading from "./modules/common/heading/ListHeading";
import List from "./modules/list";
import Details from "./modules/details";
import SuspenseFallback from "./modules/common/SuspenseFallback";

function App() {
  const detailsComponent = (
    <Suspense fallback={<SuspenseFallback />}>
      <Details />
    </Suspense>
  );
  return (
    <SWRConfig
      value={{
        revalidateOnFocus: false
      }}
    >
      <Router>
        <ThemeProvider theme={CustomTheme}>
          <CSSReset />
          <MainLayout>
            <Switch>
              <Route exact path="/">
                <ListHeading
                  title="Marvel characters list"
                  subtitle="Browse details and add to your favorites"
                />
                <Suspense fallback={<SuspenseFallback />}>
                  <List />
                </Suspense>
              </Route>
              <Route path="/details/:id" component={detailsComponent}></Route>
            </Switch>
          </MainLayout>
        </ThemeProvider>
      </Router>
    </SWRConfig>
  );
}

export default App;
