import React from "react";
import DetailsHeading from "../heading/DetailsHeading";
import { Text, Flex, List, ListItem } from "@chakra-ui/core";

const Details = () => {
  const description =
    "Rick Jones has been Hulk's best bud since day one, but now he's more than a friend...he's a teammate! Transformed by a Gamma energy explosion, A-Bomb's thick, armored skin is just as strong and powerful as it is blue. And when he curls into action, he uses it like a giant bowling ball of destruction! ";
  return (
    <Flex flexDirection="column">
      <DetailsHeading
        name="MA-Bomb (HAS)"
        thumb="http://i.annihil.us/u/prod/marvel/i/mg/3/20/5232158de5b16/standard_medium.jpg"
      />
      <Text color="gray.500" as={description ? "" : "i"} textTransform="italic">
        {description || "description not available"}
      </Text>
      <List styleType="disc">
        <ListItem>Lorem ipsum dolor sit amet</ListItem>
        <ListItem>Consectetur adipiscing elit</ListItem>
        <ListItem>Integer molestie lorem at massa</ListItem>
        <ListItem>Facilisis in pretium nisl aliquet</ListItem>
      </List>
    </Flex>
  );
};

export default Details;
