import React from "react";
import { Flex, Box, Avatar, Text, Icon } from "@chakra-ui/core";

const ListItem = ({ name, description, comics, series, events, stories }) => {
  return (
    <Flex alignItems="center" mb={6}>
      <Avatar name="Dan Abrahmov" src="https://bit.ly/dan-abramov" />
      <Box ml={4} width="100%">
        <Text color="gray.900">{name}</Text>
        {/* <Text
          color="gray.500"
          as={description ? "" : "i"}
          textTransform="italic"
        >
          {description || "description not available"}
        </Text> */}
        <Box
          color="gray.500"
          letterSpacing="wide"
          fontSize="xs"
          textTransform="uppercase"
          mt={1}
        >
          {comics.available} comics &bull; {series.available} series &bull;{" "}
          {events.available} events &bull; {stories.available} stories
        </Box>
      </Box>
      <Flex px={10} alignContent="flex-end">
        {/* <Icon name="star" color="gray.200" cursor="pointer" /> */}
        <Icon name="star" color="yellow.300" cursor="pointer" />
      </Flex>
    </Flex>
  );
};

export default ListItem;
