import React from "react";
import { Flex, Text, Box } from "@chakra-ui/core";

const Heading = ({ title, subtitle }) => {
  return (
    <Flex flexDirection="column">
      <Text
        fontWeight="medium"
        color="gray.800"
        fontSize="title"
        lineHeights="none"
        mb={0}
      >
        {title}
      </Text>
      <Text color="gray.600" fontSize="md" lineHeights="tall">
        {subtitle}
      </Text>
    </Flex>
  );
};

export default Heading;
