import React from "react";
import { Flex, Text, Box, Avatar } from "@chakra-ui/core";

const DetailsHeading = ({ name, thumb }) => {
  return (
    <Flex flexDirection="column">
      <Flex alignItems="center">
        <Avatar src={thumb} size="xl" />
        <Flex flexDirection="column" ml={4}>
          <Text
            fontWeight="medium"
            color="gray.800"
            fontSize="title"
            lineHeights="none"
            mb={0}
          >
            {name}
          </Text>
          <Text color="gray.600" fontSize="md" lineHeights="tall">
            Hola this is the subtitle
          </Text>
        </Flex>
      </Flex>
      <Box my={8} height="0" borderBottom="2px" borderColor="gray.100" />
    </Flex>
  );
};

export default DetailsHeading;
