import React from "react";
import { Stack, Text, Box } from "@chakra-ui/core";

const Heading = ({ title, subtitle }) => {
  return (
    <Stack>
      <Text
        fontWeight="medium"
        color="gray.800"
        fontSize="title"
        lineHeights="none"
        margin={{ bottom: 0 }}
        as="h1"
      >
        {title}
      </Text>
      <Text color="gray.600" fontSize="md" lineHeights="tall">
        {subtitle}
      </Text>
    </Stack>
  );
};

export default Heading;
