import React from "react";
import { Box, Button } from "@chakra-ui/core";
import useSWR from "swr";
import Heading from "../common/Heading";
import List from "../common/list";

const MainLayout = props => {
  const {
    data: { data },
    error
  } = useSWR(
    "https://gateway.marvel.com:443/v1/public/characters?apikey=2f1fed568f42c3ac72c3e50846da7949",
    fetch
  );
  return (
    <Box p={10}>
      <Heading
        title="Marvel characters list"
        subtitle="Browse details and add then to your favorites"
      />
      <Box my={8} height="0" borderBottom="2px" borderColor="gray.100" />
      {data && <List data={data} />}
      <Button mt={3} w="100%">
        Load more
      </Button>
    </Box>
  );
};

export default MainLayout;
